-- Adding foreign key constraint for person_id in person_campaigns referencing person\nALTER TABLE [dbo].[person_campaigns]\nADD CONSTRAINT [FK_person_campaigns_person]\nFOREIGN KEY ([person_id])\nREFERENCES [dbo].[person] ([person_id]);\n\n-- Adding foreign key constraint for d_campaign_id in person_campaigns referencing d_campaign\nALTER TABLE [dbo].[person_campaigns]\nADD CONSTRAINT [FK_person_campaigns_d_campaign]\nFOREIGN KEY ([d_campaign_id])\nREFERENCES [dbo].[d_campaign] ([d_campaign_id]);\n\n-- Adding foreign key constraint for d_disposition_id in person_campaigns referencing d_disposition (if this column exists)\nIF EXISTS (SELECT 1 \n           FROM INFORMATION_SCHEMA.COLUMNS \n           WHERE TABLE_NAME = 'person_campaigns' AND COLUMN_NAME = 'd_physician_disposition_id')\nBEGIN\n    ALTER TABLE [dbo].[person_campaigns]\n    ADD CONSTRAINT [FK_person_campaigns_d_disposition]\n    FOREIGN KEY ([d_physician_disposition_id])\n    REFERENCES [dbo].[d_disposition] ([d_disposition_id]);\nEND;