-- Restore from full backup\nRESTORE DATABASE YourDatabaseName\nFROM DISK = 'C:\Path\To\FullBackup.bak'\nWITH NORECOVERY;\n\n-- Restore differential backup\nRESTORE DATABASE YourDatabaseName\nFROM DISK = 'C:\Path\To\DifferentialBackup.bak'\nWITH NORECOVERY;\n\n-- Apply transaction log backups in sequence\nRESTORE LOG YourDatabaseName\nFROM DISK = 'C:\Path\To\LogFile1.trn'\nWITH NORECOVERY;\nRESTORE LOG YourDatabaseName\nFROM DISK = 'C:\Path\To\LogFile2.trn'\nWITH NORECOVERY;\n-- Continue with additional logs as needed up to the last one before 1 hour mark\n\n-- Final transaction log backup with RECOVERY\nRESTORE LOG YourDatabaseName\nFROM DISK = 'C:\Path\To\FinalLogFileBeforeHour.trn'\nWITH RECOVERY;