SELECT \n    *,\n    (3958 * acos(\n        cos(radians($origin_lat)) * cos(radians(lat)) * \n        cos(radians(lon) - radians($origin_lon)) + \n        sin(radians($origin_lat)) * sin(radians(lat))\n    )) AS distance\nFROM \n    zip_code\nWHERE \n    (3958 * acos(\n        cos(radians($origin_lat)) * cos(radians(lat)) * \n        cos(radians(lon) - radians($origin_lon)) + \n        sin(radians($origin_lat)) * sin(radians(lat))\n    )) <= $distance\nLIMIT 10;