-- Disable foreign key check\nSET foreign_key_checks = 0;\n\n-- Create tables\nCREATE TABLE IF NOT EXISTS blocks (\n    id INT UNSIGNED NOT NULL AUTO_INCREMENT,\n    title VARCHAR(100),\n    defaultpage INT UNSIGNED NOT NULL,\n    PRIMARY KEY(id)\n) ENGINE=InnoDB;\n\nCREATE TABLE IF NOT EXISTS pages (\n    pageID INT UNSIGNED NOT NULL AUTO_INCREMENT,\n    title VARCHAR(50) NOT NULL,\n    content BLOB,\n    blockid INT UNSIGNED NOT NULL,\n    FOREIGN KEY(blockid) REFERENCES blocks(id),\n    PRIMARY KEY(pageID)\n) ENGINE=InnoDB;\n\n-- Add foreign key constraint to the first table\nALTER TABLE blocks\nADD CONSTRAINT fk_defaultpage\nFOREIGN KEY (defaultpage) REFERENCES pages(pageID);\n\n-- Re-enable foreign key check\nSET foreign_key_checks = 1;