DECLARE\n       l_result B.your_package.tt_rad;\n       i NUMBER;\n   BEGIN\n       B.your_package.get_data(l_result);\n\n       FOR i IN 1..l_result.COUNT LOOP\n           DBMS_OUTPUT.PUT_LINE('Name: ' || l_result(i).name ||\n                                ', Code: ' || TO_CHAR(l_result(i).code) ||\n                                ', Vendor: ' || TO_CHAR(l_result(i).vendor) ||\n                                ', Val: ' || l_result(i).val ||\n                                ', Num: ' || TO_CHAR(l_result(i).num));\n       END LOOP;\n   END;