-- Create a table to store the deleted records along with metadata about who/when it happened.\nCREATE TABLE DeletedLogs (\n    LogID INT IDENTITY(1,1) PRIMARY KEY,\n    TableName NVARCHAR(255),\n    UserID NVARCHAR(100),\n    OperationDate DATETIME DEFAULT GETDATE(),\n    DeletedData VARCHAR(MAX)\n);\n\n-- Create a sample table\nCREATE TABLE SampleTable (\n    ID INT PRIMARY KEY,\n    Data VARCHAR(100)\n);\n\n-- Insert some data into the sample table\nINSERT INTO SampleTable (ID, Data) VALUES (1, 'Test Data');\n\n-- Create a trigger to log deletions from SampleTable\nCREATE TRIGGER trg_LogDeletion ON SampleTable FOR DELETE\nAS\nBEGIN\n    DECLARE @sqlDeletedData NVARCHAR(MAX);\n    SELECT @sqlDeletedData = (\n        SELECT * FROM DELETED FOR XML AUTO, ELEMENTS\n    );\n\n    INSERT INTO DeletedLogs (TableName, UserID, DeletedData)\n    VALUES ('SampleTable', USER_NAME(), @sqlDeletedData);\nEND;\nGO\n\n-- Test the trigger\nDELETE FROM SampleTable WHERE ID = 1;\n\n-- Check the log table to see what was deleted and by whom\nSELECT * FROM DeletedLogs;