CREATE OR REPLACE TRIGGER example_table_log_changes\n   AFTER INSERT OR UPDATE OR DELETE ON ExampleTable\n   FOR EACH ROW\n   DECLARE\n       l_operation CHAR(1);\n       l_old_value CLOB;\n       l_new_value CLOB;\n   BEGIN\n       IF DELETING THEN\n           l_old_value := :OLD.COLUMN1 || ',' || :OLD.COLUMN2; -- Concatenate columns as needed\n           l_new_value := NULL;\n           l_operation := 'D';\n       ELSIF INSERTING THEN\n           l_old_value := NULL;\n           l_new_value := :NEW.COLUMN1 || ',' || :NEW.COLUMN2; -- Concatenate columns as needed\n           l_operation := 'I';\n       ELSE -- UPDATING\n           l_old_value := :OLD.COLUMN1 || ',' || :OLD.COLUMN2; -- Concatenate columns as needed\n           l_new_value := :NEW.COLUMN1 || ',' || :NEW.COLUMN2; -- Concatenate columns as needed\n           l_operation := 'U';\n       END IF;\n\n       INSERT INTO ChangeHistory (TableName, Operation, ChangeTime, OldValue, NewValue, UserName)\n       VALUES ('ExampleTable', l_operation, CURRENT_TIMESTAMP, l_old_value, l_new_value, SYS_CONTEXT('USERENV','SESSION_USER'));\n   END;