CREATE TABLE Chart (\n    Id INT PRIMARY KEY IDENTITY,\n    ReportId INT NOT NULL, -- This can be an FK to your Report table\n    Type NVARCHAR(50) NOT NULL, -- Store the type of chart for easier querying\n    -- other common fields...\n);\n\nCREATE TABLE LineChart (\n    ChartId INT PRIMARY KEY,\n    Thickness DECIMAL,\n    YAxis DECIMAL,\n    XAxis DECIMAL,\n    Color NVARCHAR(50),\n    FOREIGN KEY (ChartId) REFERENCES Chart(Id)\n);\n\nCREATE TABLE PieChart (\n    ChartId INT PRIMARY KEY,\n    Radius DECIMAL,\n    Color NVARCHAR(50),\n    FOREIGN KEY (ChartId) REFERENCES Chart(Id)\n);\n\nCREATE TABLE BarChart (\n    ChartId INT PRIMARY KEY,\n    Width DECIMAL,\n    Color NVARCHAR(50),\n    Border NVARCHAR(50),\n    FOREIGN KEY (ChartId) REFERENCES Chart(Id)\n);