-- Define logical connection to SQL Server\nsp_addremoteserver sqlserver_conn,\n    @servername = 'SQLSERVER_INSTANCE',\n    @datasource = '192.168.1.10',\n    @pubnickname = 'sql_server';\n\n-- Define a publication (source database)\nsp_addpublication 'MyPublication',\n    @publisher_sysid = 'sybase_publisher',\n    @description = ' Publication from SQL Server',\n    @repl_freq = 'manual',\n    @status = 16,\n    @login = 'replicator_user',\n    @passwd = 'secure_password';\n\n-- Define a subscription (target database)\nsp_addsubscription 'MyPublication',\n    @subscriber_type = 'syb_rep_agent',\n    @destination_db = 'TargetDB',\n    @repl_type = 'one_way',\n    @status = 16,\n    @login = 'replicator_user',\n    @passwd = 'secure_password';\n\n-- Start replication agent\nsp_startpublication_agent 'MyPublication';