-- Step 1: Identify desired associations (in this case, assume we want item 1 only associated with category 2)\nDELETE FROM map\nWHERE itemId = 1 AND catId NOT IN (2);\n\n-- Step 2 is implicitly handled by the previous step\n\n-- Step 3: Add new associations if necessary (if adding a new association not already present)\nINSERT INTO map (itemId, catId)\nSELECT 1, c.catId\nFROM categories c\nWHERE c.catId = 2 AND NOT EXISTS (\n    SELECT 1 FROM map m WHERE m.itemId = 1 AND m.catId = c.catId\n);