-- Enable xp_cmdshell (must be run as administrator)\nEXEC sp_configure 'show advanced options', 1;\nRECONFIGURE;\nEXEC sp_configure 'xp_cmdshell', 1;\nRECONFIGURE;\n\n-- Use xp_cmdshell to execute an external program based on a condition\nDECLARE @command NVARCHAR(1000);\nIF EXISTS (SELECT * FROM YourTable WHERE SomeCondition = 1)\nBEGIN\n    SET @command = N'C:\Path\To\YourProgram.exe';\n    EXEC xp_cmdshell @command;\nEND