BEGIN\n  DBMS_STATS.GATHER_TABLE_STATS(\n    ownname      => 'YOUR_SCHEMA',\n    tabname      => 'YOUR_PARTITIONED_TABLE',\n    partname     => 'NEW_PARTITION_NAME', -- Specify the partition name if needed\n    granularity  => 'PARTITION',          -- Gather stats for just this partition\n    method_opt   => 'FOR ALL COLUMNS SIZE AUTO',\n    cascade      => TRUE                -- Include indexes\n  );\nEND;\n/