-- Update the table by converting date strings to UNIX timestamps.\nSET @row_number = 0;\n\nUPDATE your_table t1\nJOIN (\n    SELECT id_for_update, STR_TO_DATE(date_column, '%Y-%m-%d') as date_col\n    FROM (\n        SELECT (@row_number:=@row_number + 1) AS id_for_update, date_column\n        FROM your_table\n    ) subquery\n) t2 ON t1.date_column = t2.date_col\nSET t1.date_column = UNIX_TIMESTAMP(t2.date_col);