-- Add a temporary column to act as a row identifier.\nALTER TABLE your_table ADD COLUMN temp_id INT AUTO_INCREMENT PRIMARY KEY;\n\n-- Update records by converting date strings to UNIX timestamps using the new column as an identifier.\nUPDATE your_table\nSET date_column = UNIX_TIMESTAMP(STR_TO_DATE(date_column, '%Y-%m-%d'));\n\n-- Remove the temporary column after updates are done.\nALTER TABLE your_table DROP COLUMN temp_id;