DELIMITER //\n\nCREATE PROCEDURE UpdateUser(\n    IN userId INT,\n    IN tableName VARCHAR(50),\n    IN newName VARCHAR(100),\n    IN newContactInfo VARCHAR(255),\n    IN newEmailAddress VARCHAR(255),\n    IN newPreferences VARCHAR(255)\n)\nBEGIN\n    IF tableName = 'volunteers' THEN\n        UPDATE volunteers v\n        SET \n            name = IF(newName IS NOT NULL, newName, name),\n            contact_info = IF(newContactInfo IS NOT NULL, newContactInfo, contact_info)\n        WHERE id = userId;\n    ELSEIF tableName = 'mailing_list' THEN\n        UPDATE mailing_list ml\n        SET \n            email_address = IF(newEmailAddress IS NOT NULL, newEmailAddress, email_address),\n            preferences = IF(newPreferences IS NOT NULL, newPreferences, preferences)\n        WHERE id = userId;\n    END IF;\nEND //\n\nDELIMITER ;