CREATE TABLE Students (\n    StudentID INT PRIMARY KEY AUTO_INCREMENT,\n    Name VARCHAR(255) NOT NULL\n);\n\nCREATE TABLE Subjects (\n    SubjectID INT PRIMARY KEY AUTO_INCREMENT,\n    SubjectName VARCHAR(100) NOT NULL\n);\n\nCREATE TABLE GradingPeriods (\n    PeriodID INT PRIMARY KEY AUTO_INCREMENT,\n    Description VARCHAR(50),  -- e.g., '1st Trimester', '2nd Bimester'\n    Year YEAR NOT NULL\n);\n\nCREATE TABLE Grades (\n    GradeID INT PRIMARY KEY AUTO_INCREMENT,\n    StudentID INT,\n    SubjectID INT,\n    PeriodID INT,\n    AverageGrade DECIMAL(3,2),\n    MissedClasses TINYINT,\n    RecoveringGrade DECIMAL(3,2),\n    FOREIGN KEY (StudentID) REFERENCES Students(StudentID),\n    FOREIGN KEY (SubjectID) REFERENCES Subjects(SubjectID),\n    FOREIGN KEY (PeriodID) REFERENCES GradingPeriods(PeriodID)\n);