UPDATE lessonstatus ls\nINNER JOIN user u ON ls.user_id = u.user_id\nINNER JOIN (\n    SELECT user_id \n    FROM user \n    WHERE username = 'too_many_accounts'\n) AS target_user ON 1 = 1\nSET ls.user_id = target_user.user_id\nWHERE u.last_name = 'stupid' \nAND u.first_name = 'user'\nAND u.username != 'too_many_accounts'\nAND ls.lesson_id NOT IN (\n    SELECT lesson_id \n    FROM lessonstatus \n    WHERE user_id = 1\n);