-- Step 1: Add a new column 'ID' to your table (replace 'YourTableName' with your actual table name)\nALTER TABLE YourTableName\nADD ID INT;\n\n-- Step 2: Create a procedure to populate the 'ID' column with unique values\nGO\nCREATE PROCEDURE PopulateUniqueIDs\nAS\nBEGIN\n    -- Use ROW_NUMBER() to assign unique sequential numbers to each row\n    WITH CTE AS (\n        SELECT \n            ROW_NUMBER() OVER (ORDER BY (SELECT NULL)) AS RowNum,\n            *\n        FROM \n            YourTableName\n    )\n    UPDATE CTE\n    SET ID = RowNum;\nEND;\nGO\n\n-- Step 3: Execute the procedure\nEXEC PopulateUniqueIDs;\n\n-- Clean up by dropping the procedure if no longer needed\n-- DROP PROCEDURE PopulateUniqueIDs;