-- Create a date series first (MySQL 8+)\n   WITH RECURSIVE DateSeries AS (\n     SELECT CURDATE() - INTERVAL 14 DAY as date FROM DUAL\n     UNION ALL\n     SELECT DATE_ADD(date, INTERVAL 1 DAY) FROM DateSeries \n     WHERE DATE < CURDATE()\n   )\n   SELECT ds.date, IFNULL(SUM(t.hits), 0) as total_hits\n   FROM DateSeries ds\n   LEFT JOIN your_table_name t ON ds.date = t.date\n   GROUP BY ds.date;