DECLARE @minDate DATE = (SELECT MIN(date_column) FROM your_table);\nDECLARE @maxDate DATE = (SELECT MAX(date_column) FROM your_table);\n\nWITH date_series AS (\n    SELECT value AS date\n    FROM GENERATE_SERIES(@minDate, @maxDate, INTERVAL '1 day') AS s(value)\n)\nSELECT ds.date, COUNT(t.*) AS record_count\nFROM date_series ds\nLEFT JOIN your_table t ON ds.date = t.date_column\nGROUP BY ds.date\nORDER BY ds.date;