CREATE TABLE Questions \n(\n    USER_ID integer FOREIGN KEY REFERENCES User_info(USER_ID) PRIMARY KEY CHECK (USER_ID > 0),\n    QUESTION_ID integer NOT NULL CHECK (QUESTION_ID > 0), -- Assuming this should be positive and not referencing Tags directly for primary key\n    QUESTION_BODY nvarchar(4000) NOT NULL,\n    TITLE nvarchar(60) NOT NULL, -- Ensures every question has a title\n    MODERATOR_REMOVAL boolean NOT NULL,\n    SENT_TIME varchar(15) NOT NULL\n);