-- Declare necessary variables if used in calculations outside of table operations\nDECLARE @factors TABLE (Factor float);\n-- Example: Insert factors to be used, if dynamic. Here it's static for simplicity.\nINSERT INTO @factors (Factor) VALUES (1.1);\n\n-- Perform the operation directly using an INSERT-SELECT statement\nINSERT INTO MyTable(COL1, COL2, ..., COLN)\nSELECT \n    DATEPART(year, t1.DateColumn) AS Col1DatePart,\n    t2.Value2 / f.Factor AS NewColFValue,\n    t3.Value1 + ' ' + t1.StringColumn1 AS ConcatenatedString\nFROM \n    TABLE_1 t1\nINNER JOIN \n    TABLE_2 t2 ON t1.key = t2.foreignKey\nINNER JOIN \n    TABLE_3 t3 ON t2.key = t3.foreignKey\nCROSS APPLY \n    @factors f;