-- Update for volunteers table\nUPDATE volunteers v\nSET \n    name = IF('new_name_value' IS NOT NULL, 'new_name_value', name),\n    contact_info = IF('new_contact_info' IS NOT NULL, 'new_contact_info', contact_info)\nWHERE id = 1;\n\n-- Update for mailing_list table\nUPDATE mailing_list ml\nSET \n    email_address = IF('new_email_address' IS NOT NULL, 'new_email_address', email_address),\n    preferences = IF('new_preferences' IS NOT NULL, 'new_preferences', preferences)\nWHERE id = 2;