MERGE INTO YourTableName AS target\nUSING (SELECT @ScorerID AS ScorerID, @Score AS Score) AS source\nON target.ScorerID = source.ScorerID AND CAST(target.DateCreated AS DATE) = CAST(GETDATE() AS DATE)\nWHEN MATCHED THEN \n    UPDATE SET Score = source.Score\nWHEN NOT MATCHED THEN    \n    INSERT (ScorerID, Score, DateCreated)\n    VALUES (source.ScorerID, source.Score, GETDATE());