CREATE OR REPLACE PROCEDURE main_procedure(\n    IN order_amount DECIMAL(10,2),\n    OUT final_price DECIMAL(10,2)\n)\nLANGUAGE SQL\nBEGIN\n    DECLARE discounted_total DECIMAL(10,2);\n\n    -- Calling the calculate_discount procedure\n    CALL calculate_discount(order_amount, discounted_total);\n\n    -- Use the result from calculate_discount\n    SET final_price = discounted_total + 5; -- Adding a flat shipping cost of $5 for example\n\nEND;