-- Create Tables\nCREATE TABLE users (\n    user_id INT PRIMARY KEY,\n    -- other user fields...\n);\n\nCREATE TABLE products (\n    product_id INT PRIMARY KEY,\n    -- other product fields...\n);\n\nCREATE TABLE votes (\n    vote_id SERIAL PRIMARY KEY,\n    user_id INT,\n    date TIMESTAMP DEFAULT CURRENT_TIMESTAMP,\n    FOREIGN KEY (user_id) REFERENCES users(user_id)\n);\n\nCREATE TABLE vote_details (\n    vote_detail_id SERIAL PRIMARY KEY,\n    vote_id INT,\n    product_id INT,\n    vote INT, -- assuming this is a number representing the rating\n    PRIMARY KEY (vote_id, product_id),\n    FOREIGN KEY (vote_id) REFERENCES votes(vote_id),\n    FOREIGN KEY (product_id) REFERENCES products(product_id)\n);\n\n-- Example Query: Get all votes for a specific user\nSELECT p.product_id, vd.vote \nFROM vote_details vd\nJOIN votes v ON vd.vote_id = v.vote_id\nJOIN products p ON vd.product_id = p.product_id\nWHERE v.user_id = 1;\n\n-- Example Query: Count the number of stars each product received across all users\nSELECT p.product_id, SUM(vd.vote) as total_stars \nFROM vote_details vd\nJOIN products p ON vd.product_id = p.product_id\nGROUP BY p.product_id;