CREATE PROC Read_Product \n    @ProductID uniqueidentifier = NULL,\n    @IDProductType uniqueidentifier = NULL,\n    @ProductName nvarchar(255) = NULL,\n    @Weight decimal(18,4) = NULL,\n    @Volume decimal(18,4) = NULL\nAS\nBEGIN\n    SELECT ProductID, IDProductType, ProductName, ProductCode, Weight, Volume \n    FROM tblProduct \n    WHERE (@ProductID IS NULL OR ProductID = @ProductID)\n      AND (@IDProductType IS NULL OR IDProductType = @IDProductType)\n      AND (@ProductName IS NULL OR ProductName LIKE '%' + @ProductName + '%')\n      AND (@Weight IS NULL OR Weight = @Weight)\n      AND (@Volume IS NULL OR Volume = @Volume);\nEND