SELECT d1.table_name, d1.column_name\nFROM [Database1].information_schema.columns d1\nLEFT JOIN (\n    SELECT table_name, column_name \n    FROM [Database2].information_schema.columns\n    UNION ALL\n    SELECT table_name, column_name \n    FROM [Database3].information_schema.columns\n    UNION ALL\n    SELECT table_name, column_name \n    FROM [Database4].information_schema.columns\n) d2 ON d1.table_name = d2.table_name AND d1.column_name = d2.column_name\nWHERE d2.table_name IS NULL AND d2.column_name IS NULL;