CREATE PROCEDURE GetSampleData()\nAS\nBEGIN\n    DECLARE @SQL NVARCHAR(MAX) = '';\n    \n    SELECT @SQL += 'SELECT ''' + TABLE_NAME + ''' AS Table_Name, col.DATA_TYPE, \n        (SELECT TOP 1 ' + COLUMN_NAME + ' FROM ' + TABLE_NAME + ' WHERE ' + COLUMN_NAME + ' IS NOT NULL) AS Sample_Data\n        UNION ALL '\n    FROM INFORMATION_SCHEMA.COLUMNS\n    GROUP BY TABLE_NAME;\n\n    SET @SQL = LEFT(@SQL, LEN(@SQL) - LEN(' UNION ALL ')); -- Remove the last UNION ALL\n\n    EXEC sp_executesql @SQL;\nEND;