SELECT \n    t1.item AS item1,\n    t2.item AS item2,\n    t3.item AS item3,\n    (t1.price + t2.price + t3.price) AS total_price\nFROM \n    items t1\nCROSS JOIN \n    items t2\nCROSS JOIN \n    items t3\nWHERE \n    t1.item < t2.item AND  -- Ensures unique combinations without repetition\n    t2.item < t3.item AND\n    (t1.price + t2.price + t3.price) = 70;  -- Adjust to desired sum of 70 for returning $30