-- Create table with primary key that implicitly creates a clustered index\nCREATE TABLE ExampleTable (\n    ID INT PRIMARY KEY,\n    Value NVARCHAR(50)\n);\n\n-- Drop the primary key, this will also drop the associated clustered index\nALTER TABLE ExampleTable DROP CONSTRAINT PK__ExampleT__3214EC27;\n\n-- Create table with unique constraint that implicitly creates a non-clustered index\nCREATE TABLE AnotherExampleTable (\n    ID INT UNIQUE,\n    Value NVARCHAR(50)\n);\n\n-- Drop the unique constraint, this will also drop the associated non-clustered index\nALTER TABLE AnotherExampleTable DROP CONSTRAINT UK__AnotherExampleT__3214EC27;