DECLARE\n  v_rowcount NUMBER;\nBEGIN\n  LOOP\n    UPDATE my_table\n    SET a_col = NULL\n    WHERE ROWID IN (\n      SELECT rid FROM (\n        SELECT ROWID AS rid, ROWNUM rnum \n        FROM my_table \n        WHERE some_col < some_val AND ROWNUM <= 1000\n      )\n    );\n    \n    v_rowcount := SQL%ROWCOUNT;\n    \n    COMMIT; -- Explicit commit to avoid filling transaction log\n   \n    EXIT WHEN v_rowcount = 0;\n  END LOOP;\nEND;\n/