-- Remote Schema (REMOTE_SCHEMA) Procedure\nCREATE OR REPLACE PROCEDURE wrapped_procedure (\n    p_out_values OUT VARCHAR2 -- or any other suitable type to return data\n) AS\n    v_local_type LocalType@REMOTE_SCHEMA;\nBEGIN\n    REMOTE_SCHEMA.PACKAGE.PROCEDURE(v_local_type);\n    -- Convert v_local_type to a suitable format for sending (e.g., JSON)\n    p_out_values := '{"field1": "' || v_local_type.field1 || '", "field2": ' || TO_CHAR(v_local_type.field2) || '}';\nEND;\n\n-- Local Schema (LOCAL_SCHEMA) Call\nDECLARE\n    l_incoming_data VARCHAR2(32767);\n    l_local_type LocalType;\nBEGIN\n    REMOTE_SCHEMA.wrapped_procedure@DBLINK(l_incoming_data);\n    -- Parse l_incoming_data back into LocalType (pseudocode for demonstration)\n    SELECT FROM JSON_TYPE TO_OBJECT(l_incoming_data) INTO l_local_type; -- Adjust based on actual parsing method\n    LOCAL_SCHEMA.PACKAGE.PROCEDURE(l_local_type);\nEND;