DELIMITER //\n   CREATE PROCEDURE GetUserWithSentMessages()\n   BEGIN\n     SELECT u1.name as UserName\n     FROM (SELECT u1.name, COUNT(m1.UserFromId) as SentCount \n           FROM Message m1\n           JOIN User u1 ON u1.uid = m1.UserFromId \n           GROUP BY u1.name \n           HAVING SentCount > 3 ) AS temp;\n   END //\n   DELIMITER ;\n\n   -- Call the procedure\n   CALL GetUserWithSentMessages();