SELECT \n    order_id, \n    date_due, \n    date_sent,\n    CASE\n        WHEN date_due = 0 AND date_sent = 0 THEN "opened" \n        WHEN date_sent > 0 AND (CURRENT_DATE - date_due) <= 0 THEN "sent" \n        ELSE "other"\n    END AS status \nFROM \n    orders\nWHERE \n    CASE\n        WHEN date_due = 0 AND date_sent = 0 THEN "opened" \n        WHEN date_sent > 0 AND (CURRENT_DATE - date_due) <= 0 THEN "sent" \n    END = 'sent';