-- Option 2: Signal Header Table\nCREATE TABLE signal_header (\n    signal_id NUMBER PRIMARY KEY,\n    start_time TIMESTAMP,\n    sample_rate NUMBER,\n    units VARCHAR2(50)\n);\n\n-- Raw Signal Values Table\nCREATE TABLE raw_signal_value (\n    value_id NUMBER PRIMARY KEY,\n    signal_id NUMBER,\n    offset_index NUMBER,\n    value BINARY_FLOAT, -- or BINARY_DOUBLE based on precision needs\n    FOREIGN KEY (signal_id) REFERENCES signal_header(signal_id)\n);