WITH MaxDates AS (\n    SELECT \n        PatientID,\n        MAX(TreatAuthDate) AS MaxTreatAuthDate,\n        MAX(HIPAADate) AS MaxHIPAADate,\n        MAX(DrugTestDate) AS MaxDrugTestDate\n    FROM tblSignature\n    WHERE PatientID = 12345\n    GROUP BY PatientID\n)\nSELECT \n    ts.PatientVisitID,\n    ts.TreatAuthDate,\n    ts.HIPAADate,\n    ts.DrugTestDate\nFROM tblSignature ts\nJOIN MaxDates md ON ts.PatientID = md.PatientID AND (\n    (ts.TreatAuthDate = md.MaxTreatAuthDate) OR\n    (ts.HIPAADate = md.MaxHIPAADate) OR\n    (ts.DrugTestDate = md.MaxDrugTestDate)\n)\nWHERE ts.PatientID = 12345;