-- Query for the maximum number of processes/sessions allowed\nSELECT value AS max_sessions FROM v$parameter WHERE name = 'processes';\n\n-- Query for the current number of sessions\nSELECT COUNT(*) AS current_sessions FROM v$session;\n\n-- Combined query to display both values in a single output\nSELECT \n    (SELECT value FROM v$parameter WHERE name = 'processes') - 5 AS max_sessions,\n    COUNT(*) AS current_sessions\nFROM v$session;