-- Step 1: Create Database Master Key\nUSE YourDatabaseName;\nCREATE MASTER KEY ENCRYPTION BY PASSWORD = 'StR0ngPa$$w0rd!';\n\n-- Step 2: Create Certificate or Asymmetric Key\nCREATE CERTIFICATE YourCertificate \nWITH SUBJECT = 'Encryption Cert for Sensitive Data';\n\n-- Step 3: Create Symmetric Key\nCREATE SYMMETRIC KEY YourSymmetricKey \nWITH ALGORITHM = AES_128\nENCRYPTION BY CERTIFICATE YourCertificate;\n\n-- Encryption\nOPEN SYMMETRIC KEY YourSymmetricKey DECRYPTION BY CERTIFICATE YourCertificate;\nUPDATE YourTableName SET EncryptedColumnName = EncryptByKey(Key_GUID('YourSymmetricKey'), Convert(varbinary(max), SensitiveDataColumn));\n\n-- Decryption\nSELECT CONVERT(nvarchar(max), DecryptByKey(EncryptedColumnName)) AS DecryptedValue \nFROM YourTableName;\n\nCLOSE SYMMETRIC KEY YourSymmetricKey;