DECLARE @Protocols TABLE(ProtocolID INT NOT NULL)\n\nINSERT INTO @Protocols (ProtocolID)\nSELECT ProtocolID\nFROM dbo.Protocols\nWHERE WorkplaceID = @WorkplaceID\n\nDELETE FROM dbo.SubProtocols sp\nWHERE EXISTS (\n    SELECT 1 \n    FROM @Protocols p\n    WHERE p.ProtocolID = sp.ProtocolID\n)\n\nDELETE FROM dbo.ProtocolHeaders ph\nWHERE EXISTS (\n    SELECT 1 \n    FROM @Protocols p\n    WHERE p.ProtocolID = ph.ProtocolID\n)\n\n-- Delete from other tables similarly\n\nDELETE FROM dbo.Protocols\nWHERE WorkplaceID = @WorkplaceID