SELECT \n    CASE \n        WHEN score >= 0 AND score <= 9 THEN '0-9'\n        WHEN score >= 10 AND score <= 19 THEN '10-19'\n        WHEN score >= 20 AND score <= 29 THEN '20-29'\n        -- Add more ranges as needed\n        ELSE 'Other'  -- Optional: to handle scores outside the specified range\n    END AS score_range,\n    COUNT(*) AS number_of_occurrences\nFROM \n    your_table_name\nGROUP BY \n    CASE \n        WHEN score >= 0 AND score <= 9 THEN '0-9'\n        WHEN score >= 10 AND score <= 19 THEN '10-19'\n        WHEN score >= 20 AND score <= 29 THEN '20-29'\n        -- Add more ranges as needed\n        ELSE 'Other'  -- Optional: to handle scores outside the specified range\n    END;