CREATE TRIGGER DeleteGrandChildOnParentDelete\nON Parent\nAFTER DELETE\nAS\nBEGIN\n    DELETE gc\n    FROM GrandChild gc\n    INNER JOIN Child1 c1 ON gc.Child1ID = c1.ID\n    WHERE c1.ParentID IN (SELECT ID FROM DELETED)\n\n    UNION ALL\n\n    DELETE gc\n    FROM GrandChild gc\n    INNER JOIN Child2 c2 ON gc.Child2ID = c2.ID\n    WHERE c2.ParentID IN (SELECT ID FROM DELETED)\nEND;