-- Assuming TPCMember and CTPMember are foreign keys linking to CustomerID, these fields should be indexed\n\n-- Optimized TabTPC part with additional WHERE clause if possible (remove '*' for better performance)\nSELECT \n  "TPC" AS Type, \n  TabTPC.TPCRef, \n  TabTPC.TPCBranch, \n  TabTPC.TPCDate, \n  TabTPC.TPCChequeNumber, \n  TabTPC.TPCChequeValue, \n  TabTPC.TPCFee, \n  TabTPC.TPCAction,\n  TabTPC.TPCMember, \n  tabtpc.tpcdrawer,\n  TabTPC.TPCUser,\n  TabTPC.TPCDiscount\nFROM \n  TabTPC \n  INNER JOIN TabCustomers ON TabTPC.TPCMember = TabCustomers.CustomerID\nWHERE TabTPC.SomeColumn = SomeValue -- Example WHERE condition\n\nUNION ALL \n\n-- Optimized TabCTP part with additional WHERE clause if possible\nSELECT \n "CTP" AS Type, \n  TabCTP.CTPRef, \n  TabCTP.CTPBranch, \n  TabCTP.CTPDate, \n  TabCTP.CTPChequeNumb, \n  TabCTP.CTPAmount, \n  TabCTP.CTPFee, \n  TabCTP.CTPAction,\n  TabCTP.CTPMember,\n  0 as CTPXXX,\n  TabCTP.CTPUser,\n  TabCTP.CTPDiscount\nFROM \n  TabCTP  \n  INNER JOIN TabCustomers ON Tabctp.ctpMember = TabCustomers.CustomerID\nWHERE TabCTP.SomeColumn = SomeValue -- Example WHERE condition\n\n-- Then, add the WHERE clause to your main query as efficiently as possible