-- Creating the base object type with a dummy attribute\nCREATE TYPE QUEUABLE_OBJECT_TYPE AS OBJECT (\n    dummy NUMBER, -- Dummy attribute\n\n    MEMBER FUNCTION ENQUEUE(payload IN VARCHAR2) RETURN NUMBER\n);\n/\n\n-- Creating the body of the base object type\nCREATE OR REPLACE TYPE BODY QUEUABLE_OBJECT_TYPE IS\n    MEMBER FUNCTION ENQUEUE(payload IN VARCHAR2) RETURN NUMBER IS\n    BEGIN\n        -- Base enqueue logic (can be overridden in subtypes)\n        DBMS_OUTPUT.PUT_LINE('Base Enqueue: ' || payload);\n        RETURN 0;\n    END;\nEND;\n/\n\n-- Creating a subtype with additional properties for the specific payload\nCREATE TYPE SPECIFIC_QUEUABLE_OBJECT_TYPE UNDER QUEUABLE_OBJECT_TYPE (\n    message_data VARCHAR2(100),\n\n    OVERRIDING MEMBER FUNCTION ENQUEUE(payload IN VARCHAR2) RETURN NUMBER\n);\n/\n\n-- Overriding the enqueue method in the subtype\nCREATE OR REPLACE TYPE BODY SPECIFIC_QUEUABLE_OBJECT_TYPE IS\n    OVERRIDING MEMBER FUNCTION ENQUEUE(payload IN VARCHAR2) RETURN NUMBER IS\n    BEGIN\n        -- Specific enqueue logic\n        DBMS_OUTPUT.PUT_LINE('Specific Enqueue: ' || payload);\n        RETURN 1;\n    END;\nEND;\n/