SELECT AVG(TIMESTAMPDIFF(SECOND, LL1.TIMESTAMP, LL2.TIMESTAMP)) AS average_duration_seconds\nFROM LOGIN_LOG LL1\nINNER JOIN LOGIN_LOG LL2 ON LL1.PLAYER = LL2.PLAYER AND LL1.ACTION = 'login' AND LL2.ACTION = 'logout'\nLEFT JOIN LOGIN_LOG LL3 ON LL3.PLAYER = LL1.PLAYER \n    AND LL3.TIMESTAMP > LL1.TIMESTAMP\n    AND LL3.TIMESTAMP < LL2.TIMESTAMP\n    AND LL3.ACTION = 'login'\nWHERE LL3.ID IS NULL;