SELECT\n    COALESCE(SUM(`order_items`.`quantity`), 0) as `count`,\n    `menu_items`.`name`\nFROM \n    `menu_items`\nLEFT JOIN\n    `order_items` ON `menu_items`.`id` = `order_items`.`menu_item_id`\nLEFT JOIN\n    `orders` ON `order_items`.`order_id` = `orders`.`id` AND \n               `orders`.`date` >= '2008-11-01' AND \n               `orders`.`date` <= '2008-11-30'\nGROUP BY \n    `menu_items`.`id`, \n    `menu_items`.`name`