CREATE FUNCTION Test (@top integer)\nRETURNS @ResultTable TABLE (\n    -- Define the columns that SomeTable has here\n    ID INT,\n    Column1 VARCHAR(50),\n    Column2 VARCHAR(50)\n)\nAS\nBEGIN\n    DECLARE @SQL NVARCHAR(4000)\n\n    SET @SQL = N'INSERT INTO @ResultTable SELECT TOP ' +\n               CAST(@top AS NVARCHAR) + \n               N' * FROM SomeTable'\n\n    EXEC sp_executesql @SQL\n\n    RETURN\nEND\nGO