-- Auditing Trigger Example (part of the first trigger)\nCREATE TRIGGER trg_AuditUpdate ON your_table\nAFTER UPDATE AS\nBEGIN\n    SET NOCOUNT ON;\n\n    -- Set Context Info to indicate the Audit Update is happening\n    DECLARE @context_info VARBINARY(128);\n    SET @context_info = CAST('AUDIT_UPDATE' AS VARBINARY(128));\n    SET CONTEXT_INFO @context_info;\n\n    -- Perform auditing logic here (update last modified fields, etc.)\n\nEND;\nGO\n\n-- Event Logging Trigger Example\nCREATE TRIGGER trg_LogUpdate ON your_table\nAFTER UPDATE AS\nBEGIN\n    SET NOCOUNT ON;\n\n    DECLARE @context_info VARBINARY(128);\n    SET @context_info = CONTEXT_INFO();\n\n    -- Check if the context info indicates an Audit Update is happening\n    IF (@context_info <> CAST('AUDIT_UPDATE' AS VARBINARY(128)))\n    BEGIN\n        -- Perform event logging here\n\n        INSERT INTO EventsTable (EventTime, EventDescription)\n        VALUES (GETDATE(), 'Updated record in your_table');\n    END;\n    \n    -- Reset Context Info to avoid affecting other operations\n    SET @context_info = CAST('' AS VARBINARY(128));\n    SET CONTEXT_INFO @context_info;\n\nEND;\nGO