DECLARE @str NVARCHAR(MAX)\nSET @str = 'Your string &lt here&lttest&lt;&lt;'\n\n-- Loop until no more replacements are made\nWHILE PATINDEX('%&lt[^;]%', @str) > 0\nBEGIN\n    SET @str = REPLACE(\n        LEFT(@str, PATINDEX('%&lt[^;]%', @str) + 3)\n        + '&lt;',\n        '&lt' + SUBSTRING(@str, PATINDEX('%&lt[^;]%', @str) + 4, LEN(@str)),\n        '&lt'\n    )\nEND\n\nSELECT @str