DECLARE @SourceTable TABLE (ID INT, Name NVARCHAR(100));\nINSERT INTO @SourceTable VALUES (1, 'Node1'), (2, 'Node2');\n\n-- Construct the XML using FOR XML PATH\nDECLARE @MyXml XML;\nSET @MyXml = \n(\n    SELECT ID AS [@ID], -- Attribute\n           Name AS [text()] -- Text content\n    FROM @SourceTable\n    FOR XML PATH('myNode'), ROOT('root')\n);\n\n-- If you need to add this into an existing XML structure, you can use modify\nDECLARE @ExistingXML XML;\nSET @ExistingXML = '<root></root>';\n\n-- Insert the constructed XML into the existing XML structure\nSET @ExistingXML.modify('insert (sql:variable("@MyXml")) as first into (/root)[1]');\n\nSELECT @ExistingXML AS ResultXML;